import React, { useState, useEffect } from 'react'
import { useParams, useRouteMatch } from 'react-router-dom'
import patientService from '../services/patients.js'

import Resumo from './Resumo'
import Dados from './Dados'
import Metas from './Metas'

const DadosPacientes = () => {
  const [ patient, setPatient ] = useState({})
  const [ tab, setTab ] = useState('resumo')

  const id = useParams().id

  useEffect(() => {
    patientService.getOne(id).then(response => setPatient(response))
  }, [id])


  return(
    <>
      <div>
        <div>Nome: {patient.name}</div>
        <div>Idade: </div>
        <div>Tipo de asma: </div>
        <div>Observações: </div>
        <div>Última Consulta: </div>
      </div>

      <br/>

      <ul className="nav nav-tabs">
        <li className="nav-item">
          <a className={tab === 'resumo' ? 'nav-link active' : 'nav-link'} onClick={() => setTab('resumo')} href="#resumo">Resumo</a>
        </li>
        <li className="nav-item">
          <a className={tab === 'dados' ? 'nav-link active' : 'nav-link'} onClick={() => setTab('dados')} href="#dados">Dados</a>
        </li>
        <li className="nav-item">
          <a className={tab === 'metas' ? 'nav-link active' : 'nav-link'} onClick={() => setTab('metas')} href="#metas">Metas</a>
        </li>
      </ul>

      <CaixaDados tipo={tab}/>
    </>
  )}

const CaixaDados = ({ tipo }) => {
  if (tipo === 'resumo') {
    return(<Resumo />)
  } else if (tipo === 'dados') {
    return(<Dados />)
  } else if (tipo === 'metas') {
    return(<Metas />)
  }
}

export default DadosPacientes
